import styled from "styled-components"
import React from 'react';
import Skills from "./skills";
import About from "./about";
import Education from "./education";

export default function Header({language, setLanguage}) {

    function Header() {
        const skillRef = useRef(null);
        const aboutRef = useRef(null);
        const contactRef = useRef(null);
        const educationRef = useRef(null);
        const projectsRef = useRef(null);

        const scrollToSkill = () => {
          scrollToComponent(skillRef);
        };

        const scrollToAbout = () => {
          scrollToComponent(aboutRef);
        };

        const scrollToContact = () => {
          scrollToComponent(contactRef);
        };

        const scrollToEducation = () => {
          scrollToComponent(educationRef);
        };

        const scrollToProjects = () => {
          scrollToComponent(projectsRef);
        };

        return (
          <div>
            <button onClick={scrollToSkill}>Skill</button>
            <button onClick={scrollToAbout}>About</button>
            <button onClick={scrollToContact}>Contact</button>
            <button onClick={scrollToEducation}>Education</button>
            <button onClick={scrollToProjects}>Projects</button>
          </div>
        );
      }


  return (
    <Container>
        <h1>Vinicius Pedroso dos Reis</h1>
        {language? <>
            <div>
            <h2 onClick={() => scrollToComponent(<About/>)}>Sobre mim</h2>
            <h2 onClick={() => scrollToComponent(<Education/>)}>EducaÃ§Ã£o</h2>
            <h2 onClick={() => scrollToComponent(<Skills/>)}>Habilidades</h2>
            <h2 onClick={() => scrollToComponent(<About/>)}>Projetos</h2>
            <h2 onClick={() => scrollToComponent(<About/>)}>Contatos</h2>
            <img src="/assets/brasil.png" alt="brasil logo" onClick={() => setLanguage(true)}/>
            <img src="/assets/england.png" alt="england logo" onClick={() => setLanguage(false)}/>
            </div>
        </> :
        <>
            <div>
            <h2>About</h2>
            <h2>Education</h2>
            <h2>Skills</h2>
            <h2>Projects</h2>
            <h2>Contacts</h2>
            <img src="/assets/brasil.png" alt="brasil logo" onClick={() => setLanguage(true)}/>
            <img src="/assets/england.png" alt="england logo" onClick={() => setLanguage(false)}/>
            </div>
        </>}
    </Container>
  );
}

function scrollToComponent(component) {
    component.current.scrollIntoView({ behavior: 'smooth' });
}


const Container = styled.div`
    height: 8vw;
    width: 100%;
    border-bottom: 1px solid white;
    display: flex;
    align-items: center;
    justify-content: space-between;
    position: fixed;
    background-color: #333333;

    h1{
        padding: 35px 0px 35px 3%;
        font-size: 40px;
        font-weight: 700;
        font-family: 'Times New Roman', Times, serif;
        color: white;
    }

    div {
        display: flex;
        padding-right: 5%;
    }

    h2 {
        width: auto;
        display: flex;
        flex-direction: row;
        align-items: center;
        padding-left: 10px;
        font-size: 20px;
        font-weight: 400;
        font-family: 'Times New Roman', Times, serif;
        color: white;

    }

    h2:hover {
        color: #f1c40f;
        cursor: pointer;
    }

    img {
        padding: 35px 0px 35px 3%;
        width: 32px;
        height: 32px;
    }
    img:hover {
        cursor: pointer;
    }

    @media only screen and (max-width: 900px) {

        height: 90px;

        img {
            width: 4vw;
            height: 4vw;
        }

        h1{
            font-size: 4vw;
        }

        h2{
            font-size: 2.2vw;
        }
    }

    @media only screen and (max-width: 700px) {
        height: 70px;
    }
    @media only screen and (max-width: 500px) {
        height: 50px;
    }
    @media only screen and (max-width: 350px) {
        height: 35px;
    }
    @media only screen and (max-width: 280px) {
        height: 28px;
    }
`


